#!/usr/bin/env bash
set -euo pipefail
WORKSPACE="/home/kavia/workspace/code-generation/lms_dt3-307785-309184/WebAppFrontend"
cd "$WORKSPACE"
RUN_DIR="$WORKSPACE/.run"; mkdir -p "$RUN_DIR"
export CI=1
# detect App file extension
APP_IMPORT="../App"
EXT="js"
if [ -f src/App.tsx ] || [ -f src/App.ts ]; then EXT="tsx"; APP_IMPORT="../App"; fi
if [ ! -f src/__tests__/App.smoke.test.$EXT ]; then mkdir -p src/__tests__
  if [ "$EXT" = "tsx" ]; then
    cat > src/__tests__/App.smoke.test.tsx <<'TS'
import React from 'react';
import { render } from '@testing-library/react';
import App from '../App';

test('App renders without throwing', () => {
  expect(() => render(<App />)).not.toThrow();
});
TS
  else
    cat > src/__tests__/App.smoke.test.js <<'JS'
import React from 'react';
import { render } from '@testing-library/react';
import App from '../App';

test('App renders without throwing', () => {
  expect(() => render(<App />)).not.toThrow();
});
JS
  fi
fi
# run tests non-interactively using detected package manager
if [ -f yarn.lock ]; then yarn test --silent --ci || exit $?; else npm run test -- --ci --silent || exit $?; fi
exit 0

